INSERT INTO restaurants (id, name, description, address, phone, cnpj, ie, image_url) VALUES
(UUID(), "Fogo de Chão", "Uma das churrascarias mais renomadas do Brasil, a Fogo de Chão oferece cortes premium preparados no autêntico fogo de chão. O rodízio inclui uma grande variedade de carnes nobres, buffet de saladas e acompanhamentos. O ambiente sofisticado e o atendimento impecável fazem a experiência ser ainda mais especial.", "Rua1", "4499999991", "99999999999991", "999999991", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "A Casa do Porco", "Considerado um dos melhores restaurantes do mundo, A Casa do Porco oferece pratos criativos à base de carne suína. O chef Jefferson Rueda transforma o porco em uma experiência gastronômica única, com destaque para o sanduíche Porco San e o famoso 'Porco Sanzé'.", "Rua2", "4499999992", "99999999999992", "999999992", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Mocotó", "Especialista na culinária nordestina, o Mocotó é conhecido pelo seu baião de dois, escondidinho de carne seca e a famosa cachaça artesanal. O ambiente é descontraído e acolhedor, perfeito para quem quer provar o melhor da comida nordestina em São Paulo.", "Rua3", "4499999993", "99999999999993", "999999993", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Barbacoa", "Uma das melhores churrascarias de São Paulo, com carnes nobres selecionadas e um buffet sofisticado de saladas e acompanhamentos. A casa tem um ambiente elegante e serviço impecável.", "Rua4", "4499999994", "99999999999994", "999999994", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Coco Bambu", "Especializado em frutos do mar, o Coco Bambu é famoso pelos pratos bem servidos, como a rede de camarão internacional. Também oferece opções de carnes e pratos diversos em um ambiente sofisticado e aconchegante.", "Rua5", "4499999995", "99999999999995", "999999995", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Fasano", "Um dos restaurantes mais sofisticados do Brasil, especializado em alta gastronomia italiana. O chef Luca Gozzani prepara pratos como o clássico risoto de trufas e massas frescas artesanais.", "Rua6", "4499999996", "99999999999996", "999999996", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "NB Steak", "Churrascaria premium com cortes nobres, serviço à la carte e um ambiente refinado. O diferencial é o conceito de rodízio sem pressa, com carnes servidas no ponto perfeito.", "Rua7", "4499999997", "99999999999997", "999999997", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Pobre Juan", "Inspirado nas parrillas argentinas, o Pobre Juan oferece cortes especiais, como o bife ancho e o ojo de bife, acompanhados de molhos típicos e batatas rústicas.", "Rua8", "4499999998", "99999999999998", "999999998", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Bráz Pizzaria", "Uma das melhores pizzarias do Brasil, famosa pelas massas aeradas e ingredientes de alta qualidade. A pizza Caprese e a Bráz são destaques do cardápio.", "Rua9", "4499999999", "99999999999999", "999999999", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Teva", "Restaurante 100% plant-based com pratos sofisticados e criativos. Destaque para o gnocchi de batata-doce com molho de castanhas e o hambúrguer de cogumelos defumados.", "Rua10", "4499999900", "99999999999000", "999999900", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Banana Verde", "Um dos mais tradicionais restaurantes vegetarianos de São Paulo, focado em ingredientes orgânicos e sazonais. Oferece pratos como moqueca de palmito pupunha e risoto de cogumelos.", "Rua11", "4499999901", "99999999999001", "999999901", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Homa", "Restaurante vegano com pratos que surpreendem pelo sabor e apresentação. Destaque para o nhoque de banana-da-terra e o estrogonofe de cogumelos.", "Rua12", "4499999902", "99999999999002", "999999902", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Pop Vegan Food", "Restaurante casual e acessível com um cardápio 100% vegano, incluindo pizzas, hambúrgueres e pratos brasileiros como feijoada vegana.", "Rua13", "4499999903", "99999999999003", "999999903", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Tessen", "Restaurante sofisticado que combina as culinárias japonesa e peruana (cozinha Nikkei). Destaque para o ceviche de polvo e o sushi trufado.", "Rua14", "4499999904", "99999999999004", "999999904", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Eataly", "Mais do que um restaurante, o Eataly é um complexo gastronômico italiano, com diversas opções de massas artesanais, pizzas napolitanas e pratos clássicos como ossobuco com polenta.", "Rua15", "4499999905", "99999999999005", "999999905", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Maní", "Restaurante estrelado da chef Helena Rizzo, que mistura técnicas contemporâneas com ingredientes brasileiros.", "Rua16", "4499999906", "99999999999006", "999999906", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Outback Steakhouse", "Famoso pelos cortes de carne suculentos e pelo pão australiano servido de cortesia. Destaques para a Ribs on the Barbie e a Bloomin’ Onion.", "Rua17", "4499999907", "99999999999007", "999999907", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Z Deli Sandwich Shop", "Especializado em sanduíches artesanais, com destaque para o pastrami e os smash burgers.", "Rua18", "4499999908", "99999999999008", "999999908", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Kinoshita", "Restaurante japonês de alta gastronomia, conhecido pelo conceito de Kappo Cuisine, que traz uma experiência refinada e pratos elaborados na frente do cliente.", "Rua19", "4499999909", "99999999999009", "999999909", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Sushi Leblon", "Um dos restaurantes japoneses mais badalados do Rio, com peixes frescos e pratos sofisticados, como o sushi de vieiras trufadas.", "Rua20", "4499999910", "99999999999010", "999999910", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Jamie’s Italian", "Restaurante do chef britânico Jamie Oliver, especializado em comida italiana rústica e saudável, com massas frescas e ingredientes orgânicos.", "Rua21", "4499999911", "99999999999011", "999999911", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "L'Entrecôte de Paris", "Famoso por servir apenas um prato: entrecôte fatiado com molho secreto e batatas fritas à vontade.", "Rua22", "4499999912", "99999999999012", "999999912", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Madero", "Conhecido pelo seu slogan 'The best burger in the world', oferece hambúrgueres artesanais e carnes grelhadas de alta qualidade.", "Rua23", "4499999913", "99999999999013", "999999913", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Komah", "Restaurante coreano moderno que traz pratos autênticos com um toque contemporâneo, como o kimchi bokkeumbap.", "Rua24", "4499999914", "99999999999014", "999999914", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Jiquitaia", "Comida brasileira autêntica em um ambiente intimista, com pratos como o picadinho e o pudim de leite.", "Rua25", "4499999915", "99999999999015", "999999915", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Camélia Òdòdó", "Comandado pela chef Renata Vanzetto, o restaurante apresenta uma proposta vegetariana criativa, com pratos como curry de legumes e tostadas com abacate.", "Rua26", "4499999916", "99999999999016", "999999916", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Tan Tan Noodle Bar", "Inspirado nos izakayas japoneses, o Tan Tan é especializado em lámen artesanal e petiscos como gyoza e tempurá. O caldo do lámen é cozido por horas para extrair o máximo de sabor.", "Rua27", "4499999917", "99999999999017", "999999917", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "La Guapa Empanadas", "Rede da chef Paola Carosella, especializada em empanadas argentinas assadas no forno. Os sabores vão desde carne clássica até versões vegetarianas com queijo e cebola caramelizada.", "Rua28", "4499999918", "99999999999018", "999999918", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s"),
(UUID(), "Bistrot de Paris", "Um autêntico bistrô francês comandado pelo chef Alain Poletto. No menu, pratos clássicos como magret de pato e crème brûlée. O ambiente remete a um restaurante parisiense tradicional.", "Rua29", "4499999919", "99999999999019", "999999919", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxM_f2WHVF5jOQCP2n1BEs2RTHE2uXHis4Xw&s");
